import React, { useState, useEffect } from "react";

import "./clickcounter.css";


function ClickCounter() {
    const [count, setCount] = useState(
      parseInt(localStorage.getItem("count")) || 0
    );

    useEffect(() => {
        const spaceBackground = document.getElementById("space-background");
        spaceBackground.addEventListener("loadedmetadata", () => {
          spaceBackground.currentTime = Math.floor(
            Math.random() * spaceBackground.duration
          );
        });
        spaceBackground.addEventListener("ended", function () {
          this.currentTime = 0;
          this.play();
        });
      }, []);

    useEffect(() => {
      localStorage.setItem("count", count);
    }, [count]);

    const handleButtonClick = () => {
      setCount(count + 1);
    };

    return (
      <div className="click-counter-container">
        <video autoPlay muted loop id="space-background">
          <source src="/spcae-bg.mp4" type="video/mp4" />
        </video>
        <div className="click-counter-content">
          <h1>Clickathon</h1>
          <p>Total Clicks: {count}</p>
          <button onClick={handleButtonClick}>Click me!</button>
        </div>
      </div>
    );
  }

  export default ClickCounter;
